import React, { useState} from "react";
// import { AuthContext } from "../../contexts/auth";

import styles from "../../css/Login.module.css";
import logo from "../../assets/imgs/logo.png";
// import Alert from "../layout/Alert";

import Cookies from 'universal-cookie';
import axios from 'axios';


const baseUrl = 'http://127.0.0.1:8000/api/login';
const cookies = new Cookies();

const Login = () => {
  // const { authenticated, login, errorMsg } = useContext(AuthContext);

  const [user, setUser] = useState("");
  const [password, setPassword] = useState("");

  // const [showMessageError, setShowMessageError] = useState(false);

  // const handleSubmit = e => {
  //   e.preventDefault();
  //   login(user, password);
  //   !authenticated ? setShowMessageError(true) : setShowMessageError(false);
  // };

  const handleChange = e => {
    this.setState({
      form: {
        ...this.state.form,
        [e.target.name]: e.target.value
      }
    });
  }

  return (
    <div className={styles.container_login}>
      <div className={styles.login_box}>
        <form onSubmit={handleChange}>

          <img src={logo} alt="logotype" />
          <label htmlFor="email">Email</label>
          <input
            type="text"
            placeholder="Enter your email address"
            autoComplete="on"
            name="email"
            id="email"
            value={user}
            onChange={e => setUser(e.target.value)}
          />
          <label htmlFor="password">Password</label>
          <input
            type="password"
            placeholder="Enter your password"
            id="password"
            value={password}
            onChange={event => setPassword(event.target.value)}
          />
          {/* {showMessageError ? (
            <Alert severity={"error"} msg={errorMsg} />
          ) : null} */}
          <div className={styles.btn_area}>
            <button className={styles.btn} onClick={() => this.iniciarSesion()}>Login</button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default Login;
