import axios from 'axios';

const instance = axios.create({
  baseURL: 'http://localhost:8000/instance',
});

export const createSession = async (username, password) => {
  return await instance.post("/sessions", { username, password })
};
 // ========= users ==============
export const getUsers = async () => {
  return await instance.get("/users")
}

export const createUser = async (user) => {
  return await instance.post("/users", user)
}

export const deleteUser = async (id) => {
  return await instance.delete(`/users/${id}`)
}
// ========== stations/macas ============
export const getStation = async (name) => {
  return await instance.get(`/stations/${name}`)
}

// ========== booking ===================
export const getMyBookings = async (username) => {
  return await instance.get(`/booking/${username}`)
}

export const getBookingsbyDay = async (date) => {
  return await instance.get(`/getbookingbyday?date=${date}`)
}

export const availableStations = async (object) => {
  return await instance.post("/booking/verify", object) // end pont para verificar as macas que estarão disponiveis
}

export const createBooking = async (object) => {
  return await instance.post("/booking", object)
}

export const deleteBooking = async (id) => {
  return await instance.delete(`/booking/${id}`)
}

export default instance
