// overall information
import React, { useState, createContext, useEffect } from "react";
import { useNavigate } from "react-router-dom";

import { api } from "../services/api";

export const AuthContext = createContext(); // creates a global context for application

export const Authprovider = ({ children }) => {
  const navigate = useNavigate();
  const [setUser] = useState(null);

  // prevent application from losing login when refreshing:
  useEffect(() => {
    const recoveredUser = localStorage.getItem("user");
    const token = localStorage.getItem("token");

    if (recoveredUser && token) {
      setUser(JSON.parse(recoveredUser));
      api.defaults.headers.Authorization = `Bearer ${token}`;
    }

  },
);

  // informações provisorias para adminstração do frontend

  //user != null authenticated = true
  //user == null authenticated = false
  return (
    <AuthContext.Provider>
      {children}
    </AuthContext.Provider>
  );
};
