import React, { useState } from "react";
import { createUser } from "../../../services/api";
import { useNavigate } from "react-router-dom";

import { Container, Row, Col, DropdownButton, Dropdown } from "react-bootstrap";
import { InputEffect } from "../../inputs/Inputs";
import { FormButton } from "../../buttons/Buttons";
import Alert from "../../layout/Alert";
import ModalCustom from "../../modal/ModalCustom";

const NewUserForm = () => {
  const [email, seEmail] = useState();
  const [pass, setPass] = useState();
  const [confirmedPass, setConfirmedPass] = useState();
  const [errorMsg, setErrorMsg] = useState("");
  const [nivelUser, setNivelUser] = useState(null);
  const [textNivelUser, setTextNivelUser] = useState("User Level");
  const [showModal, setShowModal] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (pass !== confirmedPass) {
      return setErrorMsg("Passwords don't match");
    }
    if (nivelUser === null) {
      return setErrorMsg("select user level");
    }
    const newUser = {
      email: email,
      password: pass,
      nivelUser: nivelUser,
    };
    try {
      await createUser(newUser);
      setShowModal(true);
    } catch (err) {
      setErrorMsg("failure when trying to register new user, check data or connection");
    }
  };

  const handleConfirm = () => {
    setShowModal(false)
    navigate('/')
  }

  return (
    <form onSubmit={handleSubmit}>
      <Container>
        <Row>
          <Col>
            <InputEffect
              type="text"
              textLabel="Email"
              name="email"
              handleOnChange={(e) => seEmail(e.target.value)}
            />
            <DropdownButton id="dropdown-basic-button" title={textNivelUser} variant="secondary">
              <Dropdown.Item
                onClick={(e) => {
                  setNivelUser(1);
                  setTextNivelUser("Administrador");
                }}
              >
                Admin
              </Dropdown.Item>
              <Dropdown.Item
                onClick={(e) => {
                  setNivelUser(0);
                  setTextNivelUser("Usuário");
                }}
              >
                User
              </Dropdown.Item>
            </DropdownButton>
          </Col>
          <Col>
            <InputEffect
              type="password"
              textLabel="password"
              name="password"
              handleOnChange={(e) => setPass(e.target.value)}
            />
            <InputEffect
              type="password"
              textLabel="Confirm Password"
              name="Confirmpassword"
              handleOnChange={(e) => setConfirmedPass(e.target.value)}
            />
            {errorMsg && <Alert msg={errorMsg} severity="error" />}
          </Col>
        </Row>
        <Col sm={12} style={{ textAlign: "center" }}>
          <FormButton text="Register user" />
        </Col>
        <ModalCustom
          title="user created successfully"
          setShowModal={setShowModal}
          showModal={showModal}
          handleConfirmClick={() => handleConfirm()}
        />
      </Container>
    </form>
  );
};

export default NewUserForm;
